import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/Vertex.css";

interface Props {
    vertexA : {x: number, y: number},
    vertexB : {x: number, y: number},
    offset : number,
    active : boolean,
    pick : Function
}

const Edge = (props: Props) => {

  const clicked = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    props.pick()

  }


  return (
    <line 
    onClick={(e: MouseEvent) => clicked(e)} 
    x1={props.vertexA.x + props.offset} 
    y1={props.vertexA.y + props.offset} 
    x2={props.vertexB.x + props.offset}
    y2={props.vertexB.y + props.offset} 
    strokeDasharray={0}
    stroke={props.active ? "orange" : "black"}
    strokeWidth={5}>

    </line>
  );
};

export default Edge;
