import { MouseEvent, ReactNode, useCallback, useEffect, useRef, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";
import Edge from "../Graph/Edge";
import { IGraph } from "../Graph/Graph";

interface Props { 
  graph : IGraph,
  move : Function
}


const contain = (value:number, min: number, max: number) => {
  return Math.min(Math.max(value,min),max);
}

const Canvas = (props: Props) => {

  const canvas_ref = useRef<HTMLDivElement>(null);


  const [radius, setRadius] = useState<number>(30);
  const [targetID, setTargetID] = useState<string>('');

  const [dragging, isDragging] = useState<boolean>(false);


  const get_local_position = (e: MouseEvent) => {
    const canvas = canvas_ref.current!.getBoundingClientRect();
    return {
      x: contain(e.clientX - canvas.left - radius/2,0,canvas.width - radius),
      y: contain(e.clientY - canvas.top - radius/2,0, canvas.height - radius),
    }
  }


  const move = (e: MouseEvent) => {
    if ( !dragging ) return;

    props.move(targetID, get_local_position(e));
  }



  return (
    <div 
    ref = { canvas_ref}
    className="canvas_container"
    onMouseMove={(e: MouseEvent) => move(e)}
    onMouseUp={() => isDragging(false)}>

      {Object.entries(props.graph.vertices).map((vertex) => 
        <Vertex 
          key={vertex[1].id} 
          pick={() => {setTargetID(vertex[1].id); isDragging(true)}} 
          active={targetID == vertex[1].id} 
          radius={radius} 
          position={vertex[1].position}
        ></Vertex>
      )}

      <svg width={'100%'} height={'100%'}>
          {Object.entries(props.graph.edges).map((edge) => 
            <Edge 
              key={edge[1].id} 
              pick={() => setTargetID(edge[1].id)} 
              active={targetID == edge[1].id} 
              vertexA={props.graph.vertices.filter(g => g.id == edge[1].vertices.a)[0].position} 
              vertexB={props.graph.vertices.filter(g => g.id == edge[1].vertices.b)[0].position} 
              offset={radius/2}
            ></Edge>
          )}
      </svg>



    </div>
  );
};

export default Canvas;
