import { MouseEvent, useEffect, useRef, useState } from "react";
import { inherits } from "util";
import { IGraph } from "../Graph/Graph";


interface Props { 
  graph : IGraph,
  active : string,
  setActive : Function,
  setHovered : Function
}


export const InfoBoard = (props: any) => {


  return (

      props.active && (
          (props.graph.vertices[props.active] && (
              <div>
                  <div>
                      {props.active}
                  </div>
                  <div>
                      {props.graph.vertices[props.active].symbol}
                  </div>
                  { props.graph.vertices[props.active].edges.map((edge:string) => 
                      <div  onMouseOver={(e:MouseEvent) => props.setHovered(edge)}
                      onMouseLeave={(e: MouseEvent) => props.setHovered("")}>{edge}</div>
                  )}
              </div>
          )) || (
              <div>
                  <div>
                      {props.active}
                  </div>
                  <div>
                      {props.graph.edges[props.active].weight}
                  </div>
                  <div 
                    onClick={(e:MouseEvent) => props.setActive(props.active)}
                    onMouseOver={(e:MouseEvent) => props.setHovered(props.graph.edges[props.active].vertices.a)}
                    onMouseLeave={(e: MouseEvent) => props.setHovered("")}>{props.graph.edges[props.active].vertices.a}</div>
                  <div onMouseOver={(e:MouseEvent) => props.setHovered(props.graph.edges[props.active].vertices.b)}
                      onMouseLeave={(e: MouseEvent) => props.setHovered("")}>{props.graph.edges[props.active].vertices.b}</div>
              </div>
          )
      )

  );
};


export default InfoBoard;
