import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/GraphComponent.css";

interface Props {
    position : {x: number, y: number}
    position2? : {x: number, y: number}
    offset : number,
    value : string,
    active : boolean,
    onMouseDown : Function,
    onClick : Function,
    onContextMenu : Function
}

const GraphComponent = (props: Props) => {

  const clicked = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) {
      props.onClick()
    }
  }

  const mouseDown = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) return;
    props.onMouseDown()
  }

  const contextMenu = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    props.onContextMenu();
  }

  const getCenter = (a:number, b: number) => {
    return (a + b) / 2
  }

  return (
    props.position2 ?
    <g 
    onMouseDown={(e: MouseEvent) => mouseDown(e)}
    onClick={(e: MouseEvent) => clicked(e)}
    onContextMenu={(e: MouseEvent) => contextMenu(e)}>
      <line 
        x1={props.position.x} 
        y1={props.position.y + props.offset / 2} 
        x2={props.position2.x + props.offset / 2}
        y2={props.position2.y + props.offset / 2} 
        strokeDasharray={0}
        stroke={props.active ? "orange" : "black"}
        strokeWidth={5}>
      </line>
      <foreignObject 
        textAnchor="middle" 
        style={{
          backgroundColor: 'white', 
          textAlign: 'center', 
          cursor: 'default',
          border: '1px solid',
          borderColor: props.active ? "orange" : "black",
          padding:'1px'}}
        width={'6ch'}
        height={'2.5ch'}
        fontSize='1em'
        x={'calc(' + getCenter(props.position.x + props.offset / 2, props.position2.x + props.offset / 2) + 'px - 3ch)'} 
        y={'calc(' +  getCenter(props.position.y + props.offset / 2, props.position2.y + props.offset / 2)+ 'px - 1.25ch)'}>
          {props.value}
      </foreignObject>
    </g>
    : 
    <g 
    onClick={(e: MouseEvent) => clicked(e)}
    onMouseDown={(e: MouseEvent) => mouseDown(e)}
    onContextMenu={(e: MouseEvent) => contextMenu(e)}>
      <circle 
        strokeWidth={2} 
        r={props.offset} 
        stroke="black" 
        fill="white"
        cx={props.position.x + 'px'} 
        cy={props.position.y + 'px'}>

      </circle>


    </g>
  );
};

export default GraphComponent;


/**
