import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/GraphComponent.css";

interface Props {
    position : {x: number, y: number}
    position2? : {x: number, y: number}
    offset : number
    active : boolean,
    onMouseDown : Function,
    onClick : Function,
    onContextMenu : Function
}

const GraphComponent = (props: Props) => {

  const clicked = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) {
      props.onClick()
    }
  }

  const mouseDown = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) return;
    props.onMouseDown()
  }

  const contextMenu = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    props.onContextMenu();
  }

  const getCenter = (a:number, b: number) => {
    return (a + b) / 2
  }

  return (
    props.position2 ?
    <svg>
      <line 
      id = "test"
      x1={props.position.x + props.offset} 
      y1={props.position.y + props.offset} 
      x2={props.position2.x + props.offset}
      y2={props.position2.y + props.offset} 
      strokeDasharray={0}
      stroke={props.active ? "orange" : "black"}
      strokeWidth={5}
      onClick={(e: MouseEvent) => clicked(e)}
      onMouseDown={(e: MouseEvent) => mouseDown(e)}
      onContextMenu={(e: MouseEvent) => contextMenu(e)}>
    </line>
      <foreignObject 
        textAnchor="middle" 
        style={{backgroundColor: 'white', textAlign: 'center', pointerEvents: 'none', border: '1px solid black', padding:'1px'}}
        width={'5ch'}
        height={props.offset * 2}
        fontSize='xx-small'
        x={getCenter(props.position.x, props.position2.x)} 
        y={getCenter(props.position.y, props.position2.y)}>
          -1420
      </foreignObject>
    </svg>
    : 
    <div 
      className="craph_component_container"
      style={{
          left: props.position.x + 'px',
          top: props.position.y + 'px',
          width: props.offset -2 + 'px',
          height: props.offset -2 + 'px',
          outline : '2px solid white',

          backgroundColor: props.active ? 'orange' : 'black'
      }}
      onClick={(e: MouseEvent) => clicked(e)}
      onMouseDown={(e: MouseEvent) => mouseDown(e)}
      onContextMenu={(e: MouseEvent) => contextMenu(e)}>
    </div>
  );
};

export default GraphComponent;
