import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/GraphComponent.css";

interface Props {
    position : {x: number, y: number}
    position2? : {x: number, y: number}
    offset : number,
    value : string,
    active : boolean,
    onMouseDown : Function,
    onClick : Function,
    onContextMenu : Function
}

const GraphComponent = (props: Props) => {

  const clicked = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) {
      props.onClick()
    }
  }

  const mouseDown = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) return;
    props.onMouseDown()
  }

  const contextMenu = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    props.onContextMenu();
  }

  const getCenter = (a:number, b: number) => {
    return (a + b) / 2
  }

  return (
    props.position2 ?
    <svg>
      <line 
      x1={props.position.x + props.offset / 2} 
      y1={props.position.y + props.offset / 2} 
      x2={props.position2.x + props.offset / 2}
      y2={props.position2.y + props.offset / 2} 
      strokeDasharray={0}
      stroke={props.active ? "orange" : "black"}
      strokeWidth={5}
      onClick={(e: MouseEvent) => clicked(e)}
      onMouseDown={(e: MouseEvent) => mouseDown(e)}
      onContextMenu={(e: MouseEvent) => contextMenu(e)}>
    </line>
      <foreignObject 
        textAnchor="middle" 
        style={{
          backgroundColor: 'white', 
          textAlign: 'center', 
          pointerEvents: 'none', 
          border: '1px solid',
          borderColor: props.active ? "orange" : "black",
          padding:'1px'}}
        width={'6ch'}
        height={'2.5ch'}
        fontSize='1em'
        x={'calc(' + getCenter(props.position.x + props.offset / 2, props.position2.x + props.offset / 2) + 'px - 3ch)'} 
        y={'calc(' +  getCenter(props.position.y + props.offset / 2, props.position2.y + props.offset / 2)+ 'px - 1.25ch)'}>
          {props.value}
      </foreignObject>
    </svg>
    : 
    <div 
      className="craph_component_container"
      style={{
          left: props.position.x + 'px',
          top: props.position.y + 'px',
          width: props.offset -2 + 'px',
          height: props.offset -2 + 'px',
          outline : '2px solid black',
          color: 'black',
          fontSize: props.offset - 2,
          cursor: 'default',
          display: 'flex', 
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: props.active ? 'orange' : 'white'
      }}
      onClick={(e: MouseEvent) => clicked(e)}
      onMouseDown={(e: MouseEvent) => mouseDown(e)}
      onContextMenu={(e: MouseEvent) => contextMenu(e)}>
        {props.value}
    </div>
  );
};

export default GraphComponent;
