import { MouseEvent, useEffect, useRef, useState } from "react";
import { inherits } from "util";
import { IGraph } from "../Graph/Graph";


interface Props { 
  graph : IGraph,
  active : string,
  setActive : Function,
  setHovered : Function,
  editWeight : Function,
  editLabel : Function
}


export const InfoBoard = (props: any) => {


  const containValue = (e:any, min: number, max: number) => {
    if ( e.target.value == "" ) return "";
    if ( e.target.value == 0 ) return 0;

    return Math.min(Math.max(e.target.value,min),max);
  }



  return (

      props.active && (
          (props.graph.vertices[props.active] && (
              <div>
                  <input type="text" maxLength={5} value={props.graph.vertices[props.active].symbol} onChange={(e:any) => props.editLabel(props.active,e.target.value)}></input>
                  { props.graph.vertices[props.active].edges.map((edge:string) => 
                      <div  
                        onClick={(e:MouseEvent) => props.setActive(edge)}
                        onMouseOver={(e:MouseEvent) => props.setHovered(edge)}
                        onMouseLeave={(e: MouseEvent) => props.setHovered("")}>{edge}</div>
                  )}
              </div>
          )) || (
              <div>
                  <input type="number" min={0} max={99999} value={props.graph.edges[props.active].weight} onChange={(e:any) => props.editWeight(props.active, containValue(e,0,99999))}></input>
                  {
                    Object.entries(props.graph.edges[props.active].vertices).map((vertex) => 
                      <div  
                        onClick={(e:MouseEvent) => props.setActive(vertex[1])}
                        onMouseOver={(e:MouseEvent) => props.setHovered(vertex[1])}
                        onMouseLeave={(e: MouseEvent) => props.setHovered("")}>
                        {vertex[1] as string}
                      </div>
                    )
                  }
              </div>
          )
      )

  );
};


export default InfoBoard;
