import "../../styles/Canvas/Canvas.css";

interface IVertex {
    id : string
    edges : string[]
    position : {x: number, y: number},
    radius : number,
}

interface IEdge {
    id : string
    vertices : {a: string, b: string}
    weight : number
}

interface IGraph {
    vertices : IVertex[],
    edges : IEdge[]
}

interface Props {

}

const generateID = (prefix: string, length: number) => {
    const values = "qwertyuiopasdfghjklzxcvbnmQWERTYUIOPASDFGHJKLZXCVBNM1234567890";
    var id = prefix + '-';
    for (let i = 0; i < length; i++) {
        let idx = Math.round(Math.random() * (values.length-1));
        id += values[idx];
    }
    return id;
}


const Graph = (props: Props) => {

    const [graph, updateGraph] = useState<IGraph>({vertices: [], edges: []})

    const newVertex = (position:{x:number,y:number}) => {
        const id = generateID('vertex',5);
        const vertex : IVertex = {
            id : id,
            edges : [],
            position : position,
            radius : 30,
        }

        updateGraph((prev: IGraph) => ({...prev, vertices : [...graph.vertices, vertex]}))
    }

    const newEdge = (vertexA : string, vertexB : string) => {
        const id = generateID('edge',5);
        const edge : IEdge = {
            id : id,
            vertices : {a: vertexA, b: vertexB},
            weight : 0,
        }

        const updateVerticies = graph.vertices.map(vertex => {
            if ( vertex.id == vertexA || vertex.id == vertexB ){
                return {
                    ...vertex,
                    edges : [...vertex.edges, id]
                }
            }
            return vertex;
        })

        updateGraph((prev: IGraph) => ({vertices: updateVerticies, edges : [...graph.edges, edge]}))

