import { MouseEvent, ReactNode, useCallback, useEffect, useRef, useState } from "react";
import "../../styles/Canvas/Canvas.css";

interface IVertex {
    id : string
    edges : string[]
    position : {x: number, y: number},
    radius : number,
}

interface IEdge {
    id : string
    vertices : {a: string, b: string}
    weight : number
}

interface IGraph {
    vertices : IVertex[],
    edges : IEdge[]
}

interface Props {

}

const generateID = (prefix: string, length: number) => {
    const values = "qwertyuiopasdfghjklzxcvbnmQWERTYUIOPASDFGHJKLZXCVBNM1234567890";
    var id = prefix + '-';
    for (let i = 0; i < length; i++) {
        let idx = Math.round(Math.random() * (values.length-1));
        id += values[idx];
    }
    return id;
}


const Graph = (props: Props) => {

    const [graph, updateGraph] = useState<IGraph>({vertices: [], edges: []})


    /**
     * Create a new vertex at the given position.
     * @param position 
     */
    const newVertex = (position:{x:number,y:number}) => {
        //Generate the new vertex with a unique id
        const id = generateID('vertex',5);
        const vertex : IVertex = {
            id : id,
            edges : [],
            position : position,
            radius : 30,
        }

        updateGraph((prev: IGraph) => ({...prev, vertices : [...prev.vertices, vertex]}))
    }

    /**
     * Create a new edge between the two given vertices. 
     * @param vertexA 
     * @param vertexB 
     * @returns
     */
    const newEdge = (vertexA : string, vertexB : string) => {

        //make sure that the edge is between two different vertices (could be removed in the future)
        if ( vertexA == vertexB ) return;

        //make sure that this is a unique edge, for now only one edge can exist between a given set of vertices AB. 
        if ( graph.edges.map(edge => {
            return (edge.vertices.a == vertexA && edge.vertices.b == vertexB) || (edge.vertices.b == vertexA && edge.vertices.a == vertexB);
        }).some(val => val)) return;

        //Generate the new edge with a unique id
        const id = generateID('edge',5);
        const edge : IEdge = {
            id : id,
            vertices : {a: vertexA, b: vertexB},
            weight : 0,
        }

        //update the involved vertices to be maped to this new edge
        const updateVerticies = graph.vertices.map(vertex => {
            if ( vertex.id == vertexA || vertex.id == vertexB ){
                return {
                    ...vertex,
                    edges : [...vertex.edges, id]
                }
            }
            return vertex;
        })

        updateGraph((prev: IGraph) => ({vertices: updateVerticies, edges : [...prev.edges, edge]}))

    }

