import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/Vertex.css";

interface Props {
    id : number,
    position : {x: number, y: number}
    mouse_position : {x: number, y: number}
    radius : number
    click : Function
}

const Vertex = (props: Props) => {


  useEffect(() => {
    props.position = props.mouse_position
  }, [props.mouse_position])


  return (
    <div 
    className="vertex_container"
    style={{
        left: props.position.x + 'px',
        top: props.position.y + 'px',
        width: props.radius + 'px',
        height: props.radius + 'px'
    }}
    onClick={(e: MouseEvent) => props.click(e,props.id)}
    onMouseDown={(e: MouseEvent) => props.click(e,props.id)}
    onMouseUp={(e: MouseEvent) => props.click(e,props.id)}>

    </div>
  );
};

export default Vertex;
