import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/Vertex.css";

interface Props {
    id : number,
    position : {x: number, y: number}
    radius : number
    delete : Function
    pick : Function
}

const Vertex = (props: Props) => {

  const remove = (e : MouseEvent) => {
    props.delete(props.id);
    e.stopPropagation();
    e.preventDefault();
  }


  return (
    <div 
    className="vertex_container"
    style={{
        left: props.position.x + 'px',
        top: props.position.y + 'px',
        width: props.radius + 'px',
        height: props.radius + 'px'
    }}
    onContextMenu={(e: MouseEvent) => props.delete(e)}
    onMouseDown={() => props.pick()}>

    </div>
  );
};

export default Vertex;
