import { MouseEvent, ReactNode, useCallback, useEffect, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";

interface Props {

}

const Canvas = (props: Props) => {


  const external = {
    vertex_size: 30
  }


  const [verticies, updateVerticies] = useState<{x:number,y:number}[]>([]);
  const [targetID, setTargetID] = useState<number>(-1);

  const newVertex = (e: MouseEvent) => {
    const target = e.target as HTMLElement;

    console.log(e.target);


    const position = {
      x: e.clientX - target.offsetLeft - external.vertex_size/2,
      y: e.clientY - target.offsetTop - external.vertex_size/2,
    }

    updateVerticies((prevVert: {x:number,y:number}[]) => (
      [...prevVert, position ]
    ));
  }


  const vertexClick = (e: MouseEvent, id: number) => {
    e.stopPropagation();
    e.preventDefault();

    switch (e.type) {
      case 'mousedown':
        setTargetID((old:number) => id);
        break;

      default:
        updateVerticies((prevVert: {x:number,y:number}[]) => (
          prevVert.filter((_,i) => i != id)
        ));
        break;
    }
  }


  return (
    <div 
    id = {'mainCanvas'}
    className="canvas_container"
    onClick={(e: MouseEvent) => newVertex(e)}>

      {verticies.map((vertex,i) => <Vertex key={Math.random()} id={i} click={vertexClick} position={vertex} radius={external.vertex_size}></Vertex>)}

    </div>
  );
};

export default Canvas;
