import { MouseEvent, ReactNode, useEffect, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";

interface Props {

}

const Canvas = (props: Props) => {

  const [verticies, updateVerticies] = useState(new Map<number,ReactNode>);
  const [key, setKey] : [number, Function] = useState(0);


  const newVertex = (e: MouseEvent) => {
    const target = e.target as HTMLElement;
    const position = {
      x: e.clientX - target.offsetLeft,
      y: e.clientY - target.offsetTop,
    }

    updateVerticies((verticies) => {
      verticies.set(key, <Vertex key={key} position={position}></Vertex>);
      return verticies
    })




    setKey((key: number) => (key + 1));

    const a = verticies.entries()
    console.log(a.next().value);
    console.log(a.next().value);
    console.log(a.next().value);

  }


  return (
    <div 
    className="canvas_container"
    onClick={(e: MouseEvent) => newVertex(e)}>

      {Object.entries(verticies).map(([key,vertex]) => vertex)}

    </div>
  );
};

export default Canvas;
