import { MouseEvent, ReactNode, useCallback, useEffect, useRef, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";

interface Props {

}

const ext = {
  radius : 30
}


const contain = (value:number, min: number, max: number) => {
  return Math.min(Math.max(value,min),max);
}

const Canvas = (props: Props) => {

  const canvas_ref = useRef<HTMLDivElement>(null);

  const [verticies, updateVerticies] = useState<{[id:string]:{x:number,y:number}}>({0:{x:10,y:10},1:{x:200,y:40},2:{x:60,y:250}});
  const [edges, updateEdges] = useState<{a:number,b:number}[]>([{a:0,b:1}, {a:1,b:2}]);
  const [targetID, setTargetID] = useState<string|null>(null);


  const get_local_position = (e: MouseEvent) => {
    const canvas = canvas_ref.current!.getBoundingClientRect();
    return {
      x: contain(e.clientX - canvas.left - ext.radius/2,0,canvas.width - ext.radius),
      y: contain(e.clientY - canvas.top - ext.radius/2,0, canvas.height - ext.radius),
    }
  }

  const move = (e: MouseEvent) => {
    if (targetID == null ) return;
    const n = {...verticies};
    n[targetID] = get_local_position(e);
    updateVerticies(old => n);
  }

  return (
    <div 
    ref = { canvas_ref}
    className="canvas_container"
    onMouseMove={(e: MouseEvent) => move(e)}
    onMouseUp={() => setTargetID(null)}>

      {Object.entries(verticies).map((vertex,i) => 
      {
        console.log(vertex[0]);

      return <Vertex key={vertex[0]} pick={() => setTargetID(vertex[0])} radius={ext.radius} position={vertex[1]}></Vertex>}
      )}


      <svg width={'100%'} height={'100%'}>
          {edges.map((edge,i) => <line key={Math.random()} x1={verticies[edge.a].x + ext.radius/2} y1={verticies[edge.a].y + ext.radius/2} x2={verticies[edge.b].x + ext.radius/2} y2={verticies[edge.b].y + ext.radius/2} stroke="yellow" strokeWidth={5}></line>)}
      </svg> : null



    </div>
  );
};

export default Canvas;
