import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Canvas/GraphComponent.css";
import { inherits } from "util";

interface IGraphComponent{
  id : string,
  position : {x: number, y: number}
  value : string,
  active : boolean,
  hovered : boolean,
  setHovered : Function,
  select : Function,
  delete : Function,
}


interface IPropsEdge extends IGraphComponent{
  position2 : {x: number, y: number}
  value : string,
  active : boolean,
  directed : boolean,
  weighted : boolean

}

interface IPropsVertex extends IGraphComponent{
  radius : number,
  value : string,
  active : boolean,
  edgeTo : Function,
}



function handleMouseAction(e: MouseEvent, action: Function, ctrl?: -1|1) {
  e.stopPropagation();
  e.preventDefault();

  if ( ctrl ){
    if ( ctrl == -1 && e.ctrlKey) return;
    if ( ctrl == 1 && !e.ctrlKey) return;
  }

  action();

}

const color = (props: IGraphComponent) => {return props.active ? "salmon" : props.hovered ? "skyblue" : "#F3E99F"};

export const Edge = (props: IPropsEdge) => {

  const [offset, setOffset] = useState<{x:number,y:number}>({x:0,y:0});
  const [center, setCenter] = useState<{x:number,y:number}>({x:0,y:0});


  const getCenter = (a:number, b: number) => {
    return (a + b) / 2
  }

  const normal = (p1:{x:number,y:number}, p2: {x:number,y:number}, times : number = 2) => {
    let dx = p2.x - p1.x;
    let dy = p2.y - p1.y;

    let dLength = Math.sqrt(dx ** 2 + dy**2);

    return {x : -dy/dLength * times, y : dx/dLength * times};
  }

  useEffect(() => {
    setOffset((prev) => props.directed ? normal(props.position, props.position2) : {x:0,y:0});
  }, [props.position,props.position2,props.directed])

  useEffect(() => {
    setCenter((prev) => ({
      x : getCenter(offset.x + props.position.x,offset.x + props.position2.x),
      y : getCenter(offset.y + props.position.y,offset.y + props.position2.y)
    }))
  }, [offset])

  return (
    <g 
      onClick={(e: MouseEvent) => handleMouseAction(e,props.select)}
      onContextMenu={(e: MouseEvent) => handleMouseAction(e,props.delete)}
      onMouseOver={(e:MouseEvent) => props.setHovered(props.id)}
      onMouseLeave={(e: MouseEvent) => props.setHovered("")}>
      <defs>
        <marker id='idle' orient="auto" markerWidth='10' markerHeight='4' refX={20} refY='2'>
          <path d='M0,0 V4 L6,2 Z' fill={"#F3E99F"} />
        </marker>
        <marker id='hover' orient="auto" markerWidth='10' markerHeight='4' refX={20} refY='2'>
          <path d='M0,0 V4 L6,2 Z' fill={"sk"} />
        </marker>
        <marker id='active' orient="auto" markerWidth='10' markerHeight='4' refX={20} refY='2'>
          <path d='M0,0 V4 L6,2 Z' fill={"salmon"} />
        </marker>
      </defs>
      <linearGradient 
        id="lg"         
        x1={offset.x + props.position.x + '%'} 
        y1={offset.y + props.position.y + '%'} 
        x2={offset.x + props.position2.x + '%'}
        y2={offset.y + props.position2.y + '%'} >
        <stop stop-color="yellow" offset="0" />
        <stop stop-color="green" offset="1" />
      </linearGradient>
      <line 
        x1={offset.x + props.position.x + '%'} 
        y1={offset.y + props.position.y + '%'} 
        x2={offset.x + props.position2.x + '%'}
        y2={offset.y + props.position2.y + '%'} 
        markerEnd ={props.directed ? props.active ? "url(#active)" : props.hovered ? "url(#hover)" : "url(#idle)" : ""}
        strokeDasharray={0}
        stroke={"url(#lg)"}
        strokeWidth={2}>
      </line>

      { props.weighted && <foreignObject 
        textAnchor="middle" 
        style={{
          textAlign: 'center', 
          cursor: 'default',
          color: 'white',
          padding:'1px'}}
        width={props.value.length + 1 + 'ch'}
        height={'2.5ch'}
        fontSize='1em'
        x={'calc(' + center.x + '% - ' + (props.value.length + 1)/2 + 'ch)'} 
        y={'calc(' + center.y + '% - 1.25ch)'}>
          {props.value}
      </foreignObject>}
    </g>
  );
};


const Vertex = (props: IPropsVertex) => {

  return (
    <g
    onClick={(e: MouseEvent) => handleMouseAction(e,props.edgeTo,1)}
    onMouseDown={(e: MouseEvent) => handleMouseAction(e,props.select,-1)}
    onContextMenu={(e: MouseEvent) => handleMouseAction(e,props.delete)}
    onMouseOver={(e:MouseEvent) => props.setHovered(props.id)}
    onMouseLeave={(e: MouseEvent) => props.setHovered("")}>
      <circle 
        strokeWidth= {2}
        fontSize='1em'
        r={props.radius + 'ch'} 
        stroke={color(props)}
        fill= "black"
        cx={props.position.x + '%'} 
        cy={props.position.y + '%'}>

      </circle>
      <text 
        textAnchor="middle"
        alignmentBaseline="middle"
        strokeWidth={2}
        fill="black"
        stroke={color(props)}
        cursor={'default'}
        fontWeight={1}
        fontSize={props.radius/2 + 'ch'}
        x={props.position.x + '%'}
        y={props.position.y + '%'}>
        {props.value}
      </text>

    </g>
  );
};

export default Vertex;
