import { MouseEvent, ReactNode, useCallback, useEffect, useRef, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";

interface Props {

}

const ext = {
  radius : 30
}


const contain = (value:number, min: number, max: number) => {
  return Math.min(Math.max(value,min),max);
}

const Canvas = (props: Props) => {

  const canvas_ref = useRef<HTMLDivElement>(null);

  const [verticies, updateVerticies] = useState<{x:number,y:number}[]>([]);
  const [targetID, setTargetID] = useState<number>(-1);


  const get_local_position = (e: MouseEvent) => {
    const canvas = canvas_ref.current!.getBoundingClientRect();
    return {
      x: contain(e.clientX - canvas.left - ext.radius/2,0,canvas.width - ext.radius),
      y: contain(e.clientY - canvas.top - ext.radius/2,0, canvas.height - ext.radius),
    }
  }

  const newVertex = (e: MouseEvent) => {
    updateVerticies((prevVert: {x:number,y:number}[]) => (
      [...prevVert, get_local_position(e) ]
    ));
  }


  const removeVertex = (e: MouseEvent, id : number) => {
    e.stopPropagation();
    e.preventDefault();

    updateVerticies((prevVert: {x:number,y:number}[]) => (
      prevVert.filter((_,i) => i != id)
    ));
  }


  const move = (e: MouseEvent) => {
    if (targetID == -1 ) return;
    const n = [...verticies];
    n[targetID] = get_local_position(e);
    updateVerticies(old => n);
  }

  return (
    <div 
    ref = { canvas_ref}
    className="canvas_container"
    onClick={(e: MouseEvent) => newVertex(e)}
    onMouseMove={(e: MouseEvent) => move(e)}
    onMouseUp={() => setTargetID(-1)}>

      {verticies.map((vertex,i) => <Vertex key={Math.random()} delete={(e: MouseEvent) => removeVertex(e,i)} pick={() => setTargetID(i)} radius={ext.radius} position={vertex}></Vertex>)}

      {verticies.length > 1 ? 
      <svg width={'100%'} height={'100%'}>
          <line x1={verticies[0].x + ext.radius} y1={verticies[0].y} x2={verticies[1].x} y2={verticies[1].y} stroke="yellow" strokeWidth={5}></line>
      </svg> : null
    }


    </div>
  );
};

export default Canvas;
