import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/GraphComponent.css";
import { inherits } from "util";

interface IGraphComponent{
  position : {x: number, y: number}
  value : string,
  active : boolean,
  onClick : Function,
  onContextMenu : Function
}


interface PropsEdge extends IGraphComponent{
  position2 : {x: number, y: number}
  value : string,
  active : boolean,

}

interface PropsVertex extends IGraphComponent{
  radius : number,
  value : string,
  active : boolean,
  onMouseDown : Function,
}


class MouseActions {

  public onCtrlclick(e: MouseEvent, action: Function) {
    e.stopPropagation();
    e.preventDefault();
    if ( e.ctrlKey ) action();
  }

  public onContextMenu(e: MouseEvent, action: Function) {
    e.stopPropagation();
    e.preventDefault();
    action();
  }

  public onMouseDown(e: MouseEvent,action: Function){
    e.stopPropagation();
    e.preventDefault();
    if ( e.ctrlKey ) return;
    action();
  }

}

export const Edge = (props: PropsEdge) => {

  const getCenter = (a:number, b: number) => {
    return (a + b) / 2
  }

  return (
    <g 
    onClick={(e: MouseEvent) => clicked(e)}
    onContextMenu={(e: MouseEvent) => contextMenu(e)}>
      <line 
        x1={props.position.x} 
        y1={props.position.y} 
        x2={props.position2.x}
        y2={props.position2.y} 
        strokeDasharray={0}
        stroke= "black"
        strokeWidth={props.active ? 6 : 2}>
      </line>
      <foreignObject 
        textAnchor="middle" 
        style={{
          backgroundColor: 'white', 
          textAlign: 'center', 
          cursor: 'default',
          color: "black",
          padding:'1px'}}
        width={props.value.length + 1 + 'ch'}
        height={'2.5ch'}
        fontSize='1em'
        x={'calc(' + getCenter(props.position.x, props.position2.x) + 'px - ' + (props.value.length + 1)/2 + 'ch)'} 
        y={'calc(' + getCenter(props.position.y, props.position2.y) + 'px - 1.25ch)'}>
          {props.value}
      </foreignObject>
    </g>
  );
};


const Vertex = (props: PropsVertex) => {

  const clicked = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) {
      props.onClick()
    }
  }

  const mouseDown = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) return;
    props.onMouseDown()
  }

  const contextMenu = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    props.onContextMenu();
  }


  return (
    <g 
    onClick={(e: MouseEvent) => clicked(e)}
    onMouseDown={(e: MouseEvent) => mouseDown(e)}
    onContextMenu={(e: MouseEvent) => contextMenu(e)}>
      <circle 
        strokeWidth= {props.active ? 6 : 2}
        fontSize='1em'
        r={props.radius + 'ch'} 
        stroke="black" 
        fill= "white"
        cx={props.position.x + 'px'} 
        cy={props.position.y + 'px'}>

      </circle>
      <text 
        textAnchor="middle"
        alignmentBaseline="middle"
        strokeWidth={2}
        fill="black"
        stroke="black"
        cursor={'default'}
        fontSize={props.radius/2 + 'ch'}
        x={props.position.x}
        y={props.position.y}>
        LARSB
      </text>

    </g>
  );
};

export default Vertex;


/**
 * 
 * 
 *    <div 
      className="craph_component_container"
      style={{
          left: props.position.x + 'px',
          top: props.position.y + 'px',
          width: props.offset -2 + 'px',
          height: props.offset -2 + 'px',
          outline : '2px solid black',
          color: 'black',
          fontSize: props.offset - 2,
          cursor: 'default',
          display: 'flex', 
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: props.active ? 'orange' : 'white'
      }}
      onClick={(e: MouseEvent) => clicked(e)}
      onMouseDown={(e: MouseEvent) => mouseDown(e)}
      onContextMenu={(e: MouseEvent) => contextMenu(e)}>
        {props.value}
    </div>
 */
