import { MouseEvent, useEffect, useRef, useState } from "react";
import { inherits } from "util";
import { IGraph } from "../Graph/Graph";


interface Props { 
  graph : IGraph,
  active : string,
  setActive : Function
}


export const InfoBoard = (props: any) => {


  return (

      props.active && (
          (props.graph.vertices[props.active] && (
              <div>
                  <div>
                      {props.active}
                  </div>
                  <div>
                      {props.graph.vertices[props.active].symbol}
                  </div>
                  { props.graph.vertices[props.active].edges.map((edge:string) => 
                      <div>{edge}</div>
                  )}
              </div>
          )) || (
              <div>
                  <div>
                      {props.active}
                  </div>
                  <div>
                      {props.graph.edges[props.active].weight}
                  </div>
                  <div>{props.graph.edges[props.active].vertices.a}</div>
                  <div>{props.graph.edges[props.active].vertices.b}</div>
              </div>
          )
      )

  );
};


export default InfoBoard;
