import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/GraphComponent.css";
import { inherits } from "util";

interface IGraphComponent{
  position : {x: number, y: number}
  value : string,
  active : boolean,
  select : Function,
  delete : Function
}


interface IPropsEdge extends IGraphComponent{
  position2 : {x: number, y: number}
  value : string,
  active : boolean,

}

interface IPropsVertex extends IGraphComponent{
  radius : number,
  value : string,
  active : boolean,
  edgeTo : Function,
}



function handleMouseAction(e: MouseEvent, action: Function, ctrl?: -1|1) {
  e.stopPropagation();
  e.preventDefault();

  if ( ctrl ){
    if ( ctrl == -1 && e.ctrlKey) return;
    if ( ctrl == 1 && !e.ctrlKey) return;
  }

  action();

}


export const Edge = (props: IPropsEdge) => {

  const getCenter = (a:number, b: number) => {
    return (a + b) / 2
  }

  return (
    <g 
    onClick={(e: MouseEvent) => handleMouseAction(e,props.select)}
    onContextMenu={(e: MouseEvent) => handleMouseAction(e,props.delete)}>
      <line 
        x1={props.position.x} 
        y1={props.position.y} 
        x2={props.position2.x}
        y2={props.position2.y} 
        strokeDasharray={0}
        stroke= "black"
        strokeWidth={props.active ? 6 : 2}>
      </line>
      <foreignObject 
        textAnchor="middle" 
        style={{
          backgroundColor: 'white', 
          textAlign: 'center', 
          cursor: 'default',
          color: "black",
          padding:'1px'}}
        width={props.value.length + 1 + 'ch'}
        height={'2.5ch'}
        fontSize='1em'
        x={'calc(' + getCenter(props.position.x, props.position2.x) + 'px - ' + (props.value.length + 1)/2 + 'ch)'} 
        y={'calc(' + getCenter(props.position.y, props.position2.y) + 'px - 1.25ch)'}>
          {props.value}
      </foreignObject>
    </g>
  );
};


const Vertex = (props: IPropsVertex) => {

  return (
    <g 
    onClick={(e: MouseEvent) => handleMouseAction(e,props.edgeTo,1)}
    onMouseDown={(e: MouseEvent) => handleMouseAction(e,props.select,-1)}
    onContextMenu={(e: MouseEvent) => handleMouseAction(e,props.delete)}>
      <circle 
        strokeWidth= {props.active ? 6 : 2}
        fontSize='1em'
        r={props.radius + 'ch'} 
        stroke="black" 
        fill= "white"
        cx={props.position.x + 'px'} 
        cy={props.position.y + 'px'}>

      </circle>
      <text 
        textAnchor="middle"
        alignmentBaseline="middle"
        strokeWidth={2}
        fill="black"
        stroke="black"
        cursor={'default'}
        fontWeight={0}
        fontSize={props.radius/2 + 'ch'}
        x={props.position.x}
        y={props.position.y}>
        {props.value}
      </text>

    </g>
  );
};

export default Vertex;
