import { MouseEvent, ReactNode, useCallback, useEffect, useRef, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";
import Edge,{ IGraph } from "../Graph/Graph";

interface Props { 
  graph : IGraph
}

const ext = {
  radius : 30
}


const contain = (value:number, min: number, max: number) => {
  return Math.min(Math.max(value,min),max);
}

const Canvas = (props: Props) => {

  const canvas_ref = useRef<HTMLDivElement>(null);

  const [verticies, updateVerticies] = useState<{[id:string]:{x:number,y:number}}>({s0:{x:10,y:10},s1:{x:200,y:40},s2:{x:60,y:250},s3:{x:250,y:250}});
  const [edges, updateEdges] = useState<{[id:string]: {a:string,b:string}}>({e0:{a:'s0',b:'s1'}, e1:{a:'s1',b:'s2'},e2:{a:'s1',b:'s3'}, e3:{a:'s0',b:'s3'}});
  const [targetID, setTargetID] = useState<string>('');

  const [dragging, isDragging] = useState<boolean>(false);


  const get_local_position = (e: MouseEvent) => {
    const canvas = canvas_ref.current!.getBoundingClientRect();
    return {
      x: contain(e.clientX - canvas.left - ext.radius/2,0,canvas.width - ext.radius),
      y: contain(e.clientY - canvas.top - ext.radius/2,0, canvas.height - ext.radius),
    }
  }

  const move = (e: MouseEvent) => {
    if ( !dragging ) return;
    const n = {...verticies};
    n[targetID] = get_local_position(e);
    updateVerticies(old => n);
  }


  return (
    <div 
    ref = { canvas_ref}
    className="canvas_container"
    onMouseMove={(e: MouseEvent) => move(e)}
    onMouseUp={() => isDragging(false)}>

      {Object.entries(verticies).map((vertex) => <Vertex key={vertex[0]} pick={() => {setTargetID(vertex[0]); isDragging(true)}} active={targetID == vertex[0]} radius={ext.radius} position={vertex[1]}></Vertex>)}


      <svg width={'100%'} height={'100%'}>
          {Object.entries(edges).map((edge) => <Edge key={edge[0]} pick={() => setTargetID(edge[0])} active={targetID == edge[0]} vertexA={verticies[edge[1].a]} vertexB={verticies[edge[1].b]} offset={ext.radius/2}></Edge>)}
      </svg>



    </div>
  );
};

export default Canvas;
