import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/Vertex.css";

interface Props {
    id : number,
    position : {x: number, y: number}
    delete : Function;
}

const Vertex = (props: Props) => {

  const ref = useRef<HTMLDivElement>(null);  
  const [position, setPosition] : [{x: number, y: number}, Function] = useState(props.position);  


  const remove = (e : MouseEvent) => {
    props.delete(ref.current);
    e.stopPropagation();
  }


  return (
    <div 
    ref={ref}
    className="vertex_container"
    style={{
        left: position.x + 'px',
        top: position.y + 'px',
    }}
    onClick={(e: MouseEvent) => remove(e)}>

    </div>
  );
};

export default Vertex;
