import { MouseEvent, useEffect, useRef, useState } from "react";
import "../../styles/Graph/GraphComponent.css";

interface Props {
    position : {x: number, y: number}
    position2? : {x: number, y: number}
    offset : number,
    value : string,
    active : boolean,
    onMouseDown : Function,
    onClick : Function,
    onContextMenu : Function
}

const GraphComponent = (props: Props) => {

  const [labelFontSize, setLabelFontSize] = useState<number>();

  const clicked = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) {
      props.onClick()
    }
  }

  const mouseDown = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if ( e.ctrlKey ) return;
    props.onMouseDown()
  }

  const contextMenu = (e: MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    props.onContextMenu();
  }

  const getCenter = (a:number, b: number) => {
    return (a + b) / 2
  }

  return (
    props.position2 ?
    <g 
    onMouseDown={(e: MouseEvent) => mouseDown(e)}
    onClick={(e: MouseEvent) => clicked(e)}
    onContextMenu={(e: MouseEvent) => contextMenu(e)}>
      <line 
        x1={props.position.x} 
        y1={props.position.y} 
        x2={props.position2.x}
        y2={props.position2.y} 
        strokeDasharray={0}
        stroke= "black"
        strokeWidth={props.active ? 6 : 2}>
      </line>
      <foreignObject 
        textAnchor="middle" 
        style={{
          backgroundColor: 'white', 
          textAlign: 'center', 
          cursor: 'default',
          color: "black",
          padding:'1px'}}
        width={props.value.length + 1 + 'ch'}
        height={'2.5ch'}
        fontSize='1em'
        x={'calc(' + getCenter(props.position.x, props.position2.x) + 'px - ' + (props.value.length + 1)/2 + 'ch)'} 
        y={'calc(' + getCenter(props.position.y, props.position2.y) + 'px - 1.25ch)'}>
          {props.value}
      </foreignObject>
    </g>
    : 
    <g 
    onClick={(e: MouseEvent) => clicked(e)}
    onMouseDown={(e: MouseEvent) => mouseDown(e)}
    onContextMenu={(e: MouseEvent) => contextMenu(e)}>
      <circle 
        strokeWidth= {props.active ? 6 : 2}
        fontSize='1em'
        r={props.offset + 'ch'} 
        stroke="black" 
        fill= "white"
        cx={props.position.x + 'px'} 
        cy={props.position.y + 'px'}>

      </circle>
      <text 
        textAnchor="middle"
        alignmentBaseline="middle"
        strokeWidth={2}
        fill="black"
        stroke="black"
        cursor={'default'}
        fontSize={'2.5ch'}
        x={props.position.x}
        y={props.position.y}>
        LARSB
      </text>

    </g>
  );
};

export default GraphComponent;


/**
 * 
 * 
