import { MouseEvent, ReactElement, useEffect, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";

interface Props {

}

const Canvas = (props: Props) => {

  const [verticies, updateVerticies] = useState(new Map<number,ReactElement>);
  const [key, setKey] = useState(0);


  const newVertex = (e: MouseEvent) => {
    const target = e.target as HTMLElement;
    const position = {
      x: e.clientX - target.offsetLeft,
      y: e.clientY - target.offsetTop,
    }

    updateVerticies((vert) => {
      vert.set(key, <Vertex key={key} position={position}></Vertex>)
      return vert
    })

    setKey((key: number) => (key + 1));
  }


  return (
    <div 
    className="canvas_container"
    onClick={(e: MouseEvent) => newVertex(e)}>

      {verticies.map((vertex:any) => vertex)}

    </div>
  );
};

export default Canvas;
