import { MouseEvent, ReactNode, useCallback, useEffect, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import Vertex from "../Graph/Vertex";

interface Props {

}

function makeid(length:number) {
  let result = '';
  const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  const charactersLength = characters.length;
  let counter = 0;
  while (counter < length) {
    result += characters.charAt(Math.floor(Math.random() * charactersLength));
    counter += 1;
  }
  return result;
}

const Canvas = (props: Props) => {

  const [verticies, updateVerticies] = useState<{[id: string]: ReactNode}>({});
  const [key, setKey] = useState<number>(0);

  const [, updateState] = useState({});
  const forceUpdate = useCallback(() => updateState({}), [])

  const newVertex = (e: MouseEvent) => {
    const target = e.target as HTMLElement;
    const position = {
      x: e.clientX - target.offsetLeft,
      y: e.clientY - target.offsetTop,
    }

    const id = makeid(5);

    updateVerticies((prevVert: {[id: string]: ReactNode}) => (
      {...prevVert,[id] : <Vertex key={id} id={id} delete={removeVertex} position={position}></Vertex>}
    ));

    setKey((key: number) => (key + 1));
  }


  const removeVertex = (vkey : string) => {


    updateVerticies((prevVert: {[id: string]: ReactNode}) => {
      const updatedVerts = {...verticies};
      delete updatedVerts[vkey];

      return updatedVerts;
    });


    console.log(verticies);


    forceUpdate();

  }

  return (
    <div 
    className="canvas_container"
    onClick={(e: MouseEvent) => newVertex(e)}>

      {Object.entries(verticies).map((vertex) => vertex[1])}

    </div>
  );
};

export default Canvas;
