import { MouseEvent, useRef, useState } from "react";
import "../../styles/Canvas/Canvas.css";
import { IGraph } from "../Graph/Graph";
import Vertex, { Edge } from "./GraphComponent";

interface Props { 
  graph : IGraph,
  grafEdit : {
    newVertex : Function,
    deleteVertex : Function,
    newEdge : Function,
    deleteEdge: Function,
    moveVertex: Function,
    update: Function
  },
  active : string,
  setActive : Function,
  hovered : string,
  setHoverd : Function
}


const contain = (value:number, min: number, max: number) => {
  return Math.min(Math.max(value,min),max);
}

const Canvas = (props: Props) => {

  const canvas_ref = useRef<HTMLDivElement>(null);

  const [dragging, isDragging] = useState<boolean>(false);

  const get_local_position = (e: MouseEvent) => {
    const canvas = canvas_ref.current!.getBoundingClientRect();
    
    return {
      x: contain((e.clientX - canvas.left) * 100/canvas.width,0,100),
      y: contain((e.clientY - canvas.top)* 100/canvas.height,0, 100),
    }
  }

  const move = (e: MouseEvent) => {
    if ( !dragging ) return;
    props.grafEdit.moveVertex(props.active, get_local_position(e));
  }
  


  return (
    <div 
    ref = { canvas_ref}
    className="canvas_container"
    onClick={(e: MouseEvent) => e.ctrlKey ? props.grafEdit.newVertex(get_local_position(e)) : () => {}}
    onMouseMove={(e: MouseEvent) => move(e)}
    onContextMenu={(e : MouseEvent) => {e.preventDefault();  console.log(props.graph);}}
    onMouseUp={() => isDragging(false)}>
     <svg width={'100%'} height={'100%'}>
      {Object.entries(props.graph.edges).map((edge) => 
        (!edge[1].double || props.graph.directed) && <Edge 
          key={edge[0]}
          id={edge[0]} 
          active={props.active == edge[0]}
          hovered={props.hovered == edge[0]}
          setHovered={props.setHoverd}
          value={edge[1].weight.toString()}
          directed={props.graph.directed}
          weighted={props.graph.weighted}
          position={props.graph.vertices[edge[1].vertices.a].position} 
          position2={props.graph.vertices[edge[1].vertices.b].position} 
          select={() => {props.setActive(edge[0])}}
          delete={() => props.grafEdit.deleteEdge(edge[0])}
        ></Edge>
      )}

      {Object.entries(props.graph.vertices).map((vertex) => 
        <Vertex 
          key={vertex[0]} 
          id={vertex[0]}
          select={() => {props.setActive(vertex[0]); isDragging(true)}} 
          active={props.active == vertex[0]} 
          hovered={props.hovered == vertex[0]}
          setHovered={props.setHoverd}
          radius={3} 
          value={vertex[1].symbol}
          position={vertex[1].position}
          edgeTo={() => props.grafEdit.newEdge(props.active,vertex[0])}
          delete={() => props.grafEdit.deleteVertex(vertex[0])}
        ></Vertex>
      )}
      </svg>
    


    </div>
  );
};

export default Canvas;
