export const normal = (p1:{x:number,y:number}, p2: {x:number,y:number}, times : number = 2) => {
    let dx = p2.x - p1.x;
    let dy = p2.y - p1.y;

    let dLength = Math.sqrt(dx ** 2 + dy**2);
    
    return {x : -dy/dLength * times, y : dx/dLength * times};
}


export const average = (a:number, b: number) => {
    return (a + b) / 2
}
